import React from "react";
import axios from "axios";

import logo from './logo.svg';
import './App.css';
import CssBaseline from "@mui/material/CssBaseline";
import Container from "@mui/material/Container";
import Stack from "@mui/material/Stack";
import Grid from "@mui/material/Unstable_Grid2";



// import Clientes from "./components/Clientes";
// import Counter from "./components/Counter";
import CounterProp from "./components/CounterProp";
import Title from "./components/Title";



axios.defaults.baseURL = "http://localhost:8989/";
axios.defaults.headers.common["Content-Type"] =
    "application/json;charset=utf-8";

function App() {
  const [countState, setCountState] = React.useState(0);
  const [listaPosts, setListaPosts] = React.useState([]);

  function counterState() {
    setCountState(countState + 1);
  }

  React.useEffect(() => {
    const res = axios.get("blog/api/v1/rest/post");
    res.then((query) => {
        setListaPosts(query.data);
        console.log(query.data);
    });
  }, []);


  return (
    <div className="container-lg">
    <div className="row">
        <div className="col">
            <h1>Hello World!</h1>
        </div>
    </div>
    <div className="row">
        <div className="col-4">
            <p>Count com state: {countState}</p>
            <button
                type="button"
                className="btn btn-primary"
                onClick={counterState}
            >
                Clique aqui!
            </button>
        </div>
    </div>

    <div className="row">
                {listaPosts.length > 0 && (<table className="table">
                    <thead>
                        <tr>
                            <th scope="col">#</th>
                            <th scope="col">TÃ­tulo</th>
                            <th scope="col">Post</th>
                        </tr>
                    </thead>
                    <tbody>
                        {listaPosts &&
                            listaPosts.map((post, index) => (
                                <tr key={index}>
                                    <th scope="row" key={post.id}>{post.id}</th>
                                    <td key={post.title}>{post.title}</td>
                                    <td key={post.post}>{post.post}</td>
                                </tr>
                            ))}
                    </tbody>
                </table>)}
            </div>


</div>

  );
}

export default App;
