import React from 'react';
import { useParams } from 'react-router-dom';
import axios from "axios";


const Post = () => {
    const { postId } = useParams();
    const [listaPosts, setListaPosts] = React.useState([]);
    React.useEffect(() => {
        const res = axios.get("blog/api/v1/rest/post/"+postId);
        res.then((query) => {
            listaPosts = query.data;
            console.log(listaPosts);
        });
      }, []);
  return (
    <div>
        <h1>Post #{postId}</h1>
        <div className="row">
            {listaPosts > 0 && (<table className="table">
                <thead>
                    <tr>
                        <th scope="col">ID</th>
                        <th scope="col">Título</th>
                        <th scope="col">Post</th>
                    </tr>
                </thead>
                <tbody>
                    {listaPosts &&
                        listaPosts.map((post, index) => (
                            <tr key={index}>
                                <th scope="row" key={post.id}>{post.id}</th>
                                <td key={post.title}>{post.title}</td>
                                <td key={post.post}>{post.post}</td>
                            </tr>
                        ))}
                </tbody>
            </table>)}
        </div>
      {/* Conteúdo da página "Sobre" */}
    </div>
  );
};

export default Post;
